import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { CarCrudService } from 'src/app/Services/car-crud.service';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css']
})
export class HomePageComponent {
  filteredItems:any=[];
  cars:any=[];

  searchForm=new FormGroup({
    searchTerm:new FormControl(''),
  });

  constructor(private _carCrudService:CarCrudService){
    this.getCars();
  }

  getCars(){
    this._carCrudService.getCars().subscribe((res:any)=>{
      this.cars=res;
      //completely assign it to the filtered items array so that initially all cars are displayed
      this.filteredItems=res;
      //now when the search term is entered filter from the cars array and assign again to filtered items by calling update method
      this.searchForm.valueChanges.subscribe(() => {
        this.updateFilteredItems();
      });
    })
  }

  updateFilteredItems(){
    const searchTerm=this.searchForm.value.searchTerm;
    this.filteredItems = this.cars.filter((item:any) =>
      item.maker.toLowerCase().includes(searchTerm?.toLowerCase()) ||  item.model.toLowerCase().includes(searchTerm?.toLowerCase()) || item.rentalPrice.toLowerCase().includes(searchTerm?.toLowerCase()) 
    );
    //this.cdRef.detectChanges();

  }

}
<div class="container">
    <div class="row">
        <div class="col-4">
            <h1 class="text-center mb-5 mt-3 table-bordered">Available Cars</h1>
        </div>
        <div class="col-4"></div>
        <div class="col-4 mt-3 mb-5">
            <form [formGroup]="searchForm" class="d-flex" role="search">

                <input autofocus formControlName="searchTerm" class="form-control me-2" type="search" placeholder="Enter maker or model or rental price" aria-label="Search">
                <!-- <button class="btn btn-outline-success" type="submit">Search</button> -->
            </form>
        </div>
    </div>
</div>

<div class="container">
    <table class="table table-striped table-bordered  ">
        <thead>
            <tr>
                <th>Maker</th>
                <th>Model</th>
                <th>Rental Price</th>
                <th>Click To Rent</th>

            </tr>


        </thead>
        <tbody>
            <ng-container *ngFor="let car of filteredItems">
                <tr *ngIf="car.isAvailable=='true'" >
                    <td>{{car.maker}}</td>
                    <td>{{car.model}}</td>
                    <td>{{car.rentalPrice | currency : 'INR'}}</td>
                    <td>
                        <button disabled class="btn btn-primary">Rent</button>
                    </td>

                </tr>
                <tr *ngIf="car.isAvailable=='false'" >
                    <td>{{car.maker}}</td>
                    <td>{{car.model}}</td>
                    <td>{{car.rentalPrice | currency : 'INR'}}</td>
                    <td>
                        <h4 class="text-danger">Unavailable!</h4>
                    </td>

                </tr>


            </ng-container>
            <div class="container" *ngIf="filteredItems.length==0">
                <h3 class="text-center text-success">No cars available with search term</h3>
            </div>

        </tbody>
    </table>

</div>
