import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { AuthService } from 'src/app/Services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm=new FormGroup({
    username:new FormControl('',[Validators.required]),
    password:new FormControl('',[Validators.required]),

  });

  constructor(private _authService:AuthService,private toastr: ToastrService,private _router: Router){}

  onSubmit(){
    const user={
      username:this.loginForm.value.username,
      password:this.loginForm.value.password
    }
    this._authService.login(user).subscribe((res:any)=>{
      this._authService.storeUsername(res.username);
      this.toastr.success("Successfully Login","Car Rental App ");
      if(res.username!="admin@nagarro.com"){
        this._router.navigate(['/user-home'])
      }
      else{
        this._router.navigate(['/cars'])
      }


    },
    (error)=>{
      this.toastr.error("Invalid Credentials","Car Rental App ")
    })


  }

  get username(){
    return this.loginForm.controls.username;

  }
  get password(){
    return this.loginForm.controls.password;

  }

}
<h3 class=" mt-3 mb-3">Login</h3>
<div class="container">
    <div class="row">
        <div class="col-6">
            <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
                <div class="form-group mt-2">
                    <label>Username: </label>
                    <input autofocus [class.is-invalid]="username.invalid && username.touched" formControlName="username" type="text" class="form-control" >
                    <div *ngIf="username.invalid && username.touched">
                        <small *ngIf="username.errors?.['required']" class="text-danger">Username  is required</small>
                    </div>
                </div>
                <div class="form-group mt-3">
                    <label>Password</label>
                    <input formControlName="password" [class.is-invalid]="password.invalid && password.touched"  type="password" class="form-control"  required>
                    <div *ngIf="password.invalid && password.touched">
                        <small *ngIf="password.errors?.['required']" class="text-danger">Password is required</small>
                    </div>
                </div>



                <button [disabled]="loginForm.invalid" type="submit" class="btn btn-primary mt-3">
                    Login
                </button>

            </form>

        </div>
    </div>
</div>
