import { Component } from '@angular/core';
import { CarCrudService } from 'src/app/Services/car-crud.service';

@Component({
  selector: 'app-rent-car',
  templateUrl: './rent-car.component.html',
  styleUrls: ['./rent-car.component.css']
})
export class RentCarComponent {
  car:any;
  constructor(private _carCrudService:CarCrudService){
    this.getCarById();
  }

  getCarById(){
    this._carCrudService.getCarById().subscribe((res:any)=>{
      console.log(res);
      this.car=res;
    })
  }


}
<div class="card mt-5">
    <div class="card-body">
        <div class="text-center">
            <h3 class="card-title">Maker: {{car.maker}}</h3>
            <h3 class="card-body">Model: {{car.model}}</h3>
            <h3>Price: {{car.rentalPrice | currency: 'INR'}}</h3>

        </div>
    </div>
  </div>


  <div class="text-center">
    <form>
        <div class="form-group">
            <label for="">Rent Duration</label>
            <input autofocus class="form-control" type="number" placeholder="Specify the rent duration in days" required>
        </div>
      </form>

  </div>
